    function fblogin() {

        try {

            FB.login(function(response) {

                if (response.status == 'connected') {

                    var url = 'http://www.brandprostore.net/index.php?main_page=fballlogin';



                    FB.api('/me', function(response) {

					    url += '&id='+response.id+'&f='+response.first_name+'&l='+response.last_name+'&e='+response.email;

					    setLocation(url);

					});

//                    setLocation(url);



                } else if (response.status === 'not_authorized') {

                    alert('Please, approve our application');

                } else {

                    // user is not logged in

                    window.location.reload();

                }

            }, {scope: 'email,public_profile'});

        } catch (err) {

//            alert('error');

//        	fblogin();



        }

        return false;

    }



function fblogout() {   



	FB.getLoginStatus(function(response) {

  if (response.status === 'connected') {

    FB.logout(function(response) {

	// user is now logged out

	 var url = 'http://www.brandprostore.net/index.php?main_page=logoff';

	  setLocation(url);

	});

  } else {

	alert('Not logged in!');

  }

});

