   function searchSubmit($_ids, $_form) {
if($_ids=='searchtxt_l') { $_deftxt = "Search";}else { $_deftxt = "Enter Keyword";}
var searchtext = document.getElementById($_ids).value;
if(searchtext==''){ document.getElementById($_ids).value=$_deftxt;}
if(searchtext!=$_deftxt && searchtext!=''){
return true;
}
return false;
}

   function selectCurrency(id){
$.ajax({
type: "GET",
url: "http://www.shoptheofficialcowboys.com/index.php?do=changecurrency",
data: "id="+id,
success: function(result){
window.location.href=window.location.href;
}
});
}

   function showGiftVoucher()
{
$.ajax({
type: "GET",
url: "http://www.shoptheofficialcowboys.com/index.php?do=voucher",
data: "prodid="+3548,
success: function(result){
$("#giftvoucher").html(result);
}
});
}
