head.script:
   var baseDir = 'http://www.raidersnflproshop.com/';
var http_host = baseDir.replace("http://www.","").replace("/","");
if(document.domain != http_host ){
if(document.domain != 'www.'+http_host){window.location.href='http://www.'+http_host+'/';}
}

div.script:
        $(function() {
  $('#piGal').css({
    'visibility': 'hidden'
  });

  $('#piGal').photosetGrid({
    layout: '',
    width: '430px',
    highresLinks: true,
    rel: 'pigallery',
    onComplete: function() {
      $('#piGal').css({ 'visibility': 'visible'});

      $('#piGal a').colorbox({
        maxHeight: '90%',
        maxWidth: '90%',
        rel: 'pigallery'
      });

      $('#piGal img').each(function() {
        var imgid = $(this).attr('id').substring(9);

        if ( $('#piGalDiv_' + imgid).length ) {
          $(this).parent().colorbox({ inline: true, href: "#piGalDiv_" + imgid });
        }
      });
    }
  });
});

      function sizeClick(D,E,F,A,C){	
//
	if(!(jQuery(D).hasClass("disabled"))){	//size 
		jQuery("#sizeSelect").val(E);
		jQuery("div.sizeOption").removeClass("selected");
		jQuery(D).addClass("selected");
		var B=jQuery("div.sizeLabelSizeChart > div.sizeLabel");
		B.addClass("highlight");
		if(F!=""){
			//B.html("Your choice: "+jQuery(D).html()+" + "+F);
			jQuery("div.PriceContainer .additionalPrice").html(" + "+F);
			jQuery("div.qlPriceAdjustmentContainer").html(" + "+F)
			}
		else{
			//B.html("Your choice: "+jQuery(D).html());
			jQuery("div.PriceContainer .additionalPrice").html("");
			jQuery("div.qlPriceAdjustmentContainer").html("")
			}
//		validateCartControls(C);
		}
	else{
		jQuery("div.sizeChoiceContainer > div.sizeOption").removeClass("selected");
		jQuery("#addToCart").removeClass("enabled");
		jQuery("#addToCart").addClass("disabled");
		jQuery("#sizeSelect").val("");
		var B=jQuery("div.sizeLabelSizeChart > div.sizeLabel");
		jQuery(B).html("Select Size");
		jQuery("div.PriceContainer .additionalPrice").html("");
		if(A!=""){A="

"+A}
		showError("","This item no longer available in "+jQuery(D).html()+"."+A,C)
		}
		validate($("#sizeSelect"));
}
$(function(){
$("div.sizeOption").bind('mouseenter mouseleave',function(){$(this).toggleClass('hover')});
		   });

