$(function(){
	$("form[name='cart_quantity']").submit(function(){
		var isSubmit = true;
		if($("#sizeSelect").val()==''){	isSubmit = false;
		alert("You must select a size/option for this product!");
		}
		return isSubmit;
	});	
});
