div.script:
       function changeAttr(val){
			url = location.href;
			if(val!=""){
				if(val.indexOf("http") != -1){ location.href = val;	}
				patt = "/&" + val + "=[0-9]*/";
				url = url.replace(eval(patt), "");
				patt = "/\\?" + val + "=[0-9]*/";
				url = url.replace(eval(patt), "?");
				url = url.replace("?&", "?");
				url = url.replace("/\\?$/", "");				
			}else{			
				patt = "/(\\?|&)(type_filter=[^&]*|color=[^&]*|feature=[^&]*|gender=[^&]*)/gi";
				url = url.replace(eval(patt),'');
				if(url.search(/\?/) == -1) {
					patt = "/(&)(page=[^&]*)/i";
					url = url.replace(eval(patt),'?$2');
				}			
			}
			location.href = url;
		}

      function showCatvert(display){
    	element = $(display);
		view_element = $('view_all_'+display.split('_')[2]);
		if(element.style.display == "none" ){
			new Effect.BlindDown(element,{duration:0.7});
			view_element.innerHTML = view_element.innerHTML.replace('View All...','Click to Hide...');
		} else {
			new Effect.BlindUp(element,{duration:0.5});
			view_element.innerHTML = view_element.innerHTML.replace('Click to Hide...','View All...');
		}
	}
