$(function(){
           var t = $("#text_box");
           $("#add").click(function(){
               t.val(parseInt(t.val())+1)
               if (parseInt(t.val())!=1){
                   $('#min').attr('disabled',false);
               }

           })
           $("#min").click(function(){
               t.val(parseInt(t.val())-1)
               if (parseInt(t.val())==1){
                   $('#min').attr('disabled',true);
               }
               if(parseInt(t.val())==0){
                   alert("Quantity can not be less than 1 !");
                   t.val(parseInt(t.val())+1)
               }

           })

       })

form.script:
   function update_helpful(id)
{
    $.ajax({
        type: "GET",//Get method or post method
        url: "http://www.robertalanstudio.com/index.php?main_page=product_reviews_info",//server script pathurl
        dataType:"json",//the data type of return(xml,html or text)
        data: {products_id: 125, action:'update', reviews_id:id},
        error:function(text){
            alert('server error');
        },
        success:function(result){
            if(result.status==1){
                alert(result.message);
            }else{
                $('#finger_'+id).html('helpful '+result.total+'');
            }
        }
    });
}

div.script:
     function goTopEx() {
    var obj = document.getElementById("gototop");
    function getScrollTop() {
        return document.documentElement.scrollTop || document.body.scrollTop;
    }
    function setScrollTop(value) {
        document.documentElement.scrollTop = value;document.body.scrollTop = value;
    }
    window.onscroll = function () { getScrollTop() > 0 ? obj.style.display = "block" : obj.style.display = "none"; }
    obj.onclick = function () {
        var goTop = setInterval(scrollMove, 100);
        function scrollMove() {
            setScrollTop(getScrollTop() / 6);
            if (getScrollTop() < 1) clearInterval(goTop);
        }
    }
}
$(function(){
	goTopEx();
});
