head.script:
   var baseDir = 'http://www.lionsofficialonlinestore.com/';
var http_host = baseDir.replace("http://www.","").replace("/","");
if(document.domain != http_host ){
if(document.domain != 'www.'+http_host){window.location.href='http://www.'+http_host+'/';}
}

div.script:
         $(function() {
  $('#piGal').css({
    'visibility': 'hidden'
  });

  $('#piGal').photosetGrid({
    layout: '1',
    width: '352px',
    highresLinks: true,
    rel: 'pigallery',
    onComplete: function() {
      $('#piGal').css({ 'visibility': 'visible'});

      $('#piGal a').colorbox({
        maxHeight: '90%',
        maxWidth: '90%',
        rel: 'pigallery'
      });

      $('#piGal img').each(function() {
        var imgid = $(this).attr('id').substring(9);

        if ( $('#piGalDiv_' + imgid).length ) {
          $(this).parent().colorbox({ inline: true, href: "#piGalDiv_" + imgid });
        }
      });
    }
  });
});

        function changeLable(str,num){
	var i=1;
	for(i=1;i<=num;i++){
		if(i==str){
			document.getElementById('block'+str).className="dis";
		}else{
			document.getElementById('block'+i).className="undis";
		}
	}
}
