   function compareNew(obj, action) {
    $('.compareResult22').load('ajax_compare.php', {'compare_id': obj, 'action': action, 'securityToken': 'c08bb4762a4defbd0c377ae337dc63ec'});
	$('#majun'+obj).attr("class",'icon-heart');
  }
  function compareDel(obj, action) {
    $('.compareResult22').load('ajax_compare.php', {'compare_id': obj, 'action': action, 'securityToken': 'c08bb4762a4defbd0c377ae337dc63ec'});
	$('#majun'+obj).attr("class",'icon-heart-empty');
	$('#privacy').load('ajax_compare2.php', {'compare_id': obj, 'action': action, 'securityToken': 'c08bb4762a4defbd0c377ae337dc63ec'});
  }
  function compareNew2(obj, action) {
    $('.compareResult22').load('ajax_compare.php', {'compare_id': obj, 'action': action, 'securityToken': 'c08bb4762a4defbd0c377ae337dc63ec'});
	$('#emptyicon'+obj).attr("class",'icon-heart');
  }

