   var show_mini_cart = false;

            $(function(){

                $(".cart_box").hover(function(){

                    if(!show_mini_cart)

                    { 

                        $.ajax({

                            url: "/h-cart-miniCartList.html",

                            cache: false,

                            success: function(html){

                                $('#minicartlist').html(html);

                                show_mini_cart = true;

                            }

                        })

                    }

                    $("#CartsCon").show();

                },function(){

                    $("#CartsCon").hide();

                    show_mini_cart = false;

                })

            })

