$('.boxbutton').click(function(){
        var goods_id = $('input[name="goods_id"]');
        var score = $('input[name="score"]'); 
        var nickname = $('input[name="nickname"]');
        var code = $('input[name="code"]');
        var content = $('#txt');
        if(!$.trim(nickname.val())){
            // $('#nickname_tip').css({'display':'block','float':'left'});
            nickname.tip('Please enter your name.', 5, 0, 2000);
            return false;
        }
        if(!$.trim(content.val())){
            // $('#content_tip').css({'display':'block','float':'left'});
            content.tip('Please enter your review content.', 5, 0, 2000);
            return false;
        }
        if(!$.trim(code.val())){
            // $('#code_tip').css({'display':'block','float':'left'});
            code.tip('Please enter code.', 5, 0, 2000);
            return false;
        }
            $.ajax({
                url: "/h-product-ajaxComment.html",
                type: 'POST',
                data: {
                    goods_id:goods_id.val(),
                    nickname: nickname.val(),
                    score: score.val(),
                    code:code.val(),
                    detail:content.val(),
                },
                dataType:'json',
                cache: false,
                success:function(rs){
                    if(rs.status == 1)
                    {
                        alert('Comment succeed.');
                        $("#comment_background, #comment_content").hide();
                    }else{
                        alert(rs.msg);
                        return false;
                    } 
                }
            });
        return true;
    });

    function submitNewsletter()
{
    email = $("#newsletter_email").val();
    if (email == "")
    {
        $("#newsletter_email").tip("Please enter a valid email address.", 15, 0, 2000);
        return;
    }
    $isvalidemail = /^((([a-z]|\d|[!#\$%&'\*\+\-\/=\?\^_`{\|}~]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])+(\.([a-z]|\d|[!#\$%&'\*\+\-\/=\?\^_`{\|}~]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])+)*)|((\x22)((((\x20|\x09)*(\x0d\x0a))?(\x20|\x09)+)?(([\x01-\x08\x0b\x0c\x0e-\x1f\x7f]|\x21|[\x23-\x5b]|[\x5d-\x7e]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(\\([\x01-\x09\x0b\x0c\x0d-\x7f]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF]))))*(((\x20|\x09)*(\x0d\x0a))?(\x20|\x09)+)?(\x22)))@((([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])*([a-z]|\d|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])))\.)+(([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])|(([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])([a-z]|\d|-|\.|_|~|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])*([a-z]|[\u00A0-\uD7FF\uF900-\uFDCF\uFDF0-\uFFEF])))$/i.test(email);
    if (!$isvalidemail)
    {
        $("#newsletter_email").tip("Please enter a valid email address.", 15, 0, 2000);
        return;
    }
    $.ajax({
        url: '/h-module-submitNewsletter.html',
        type: 'POST',
        data: {
            email: email
        },
        dataType: 'text',
        cache: false,
        success: function(msg) {
            if (msg == "1") {
                $("#newsletter_email").tip("Successfully subscribed, thank you for your attention to our website.", 15, 0, 2000);
            } else {
                $("#newsletter_email").tip("Subscribe failed, please try to refresh", 15, 0, 2000);
            }
        },
        error: function() {
            alert('Connection fails, please retry refresh');
        }
    });
}
